import React from 'react';

 class Input extends React.Component{
     onChangeInput=(e)=>{
        window.localStorage.setItem('answer',e.target.value )
     }
     render()
     {
         return(
             <div>
             <h6>Question 1:</h6>
             <br></br>
             <h3>Some Question</h3>
             <textarea name="second" rows="5" cols="100" title="Answer" onChange={this.onChangeInput} ></textarea>
             </div>
         );
     }
 }
 export default Input;
