import React from 'react';
import axios from 'axios';
import {questions} from './data/constants.js'

 class Input extends React.Component{
     constructor(props){
         super(props);
         this.ques =questions;
     }
     onChangeInput=(e)=>{
        window.localStorage.setItem('answer',e.target.value )
     }
     onSubmit=(e)=>{
         console.log('submitting...')
        axios.post('http://localhost:5000/ans', {ans: window.localStorage.getItem('answer')})
    .then(response => {
      console.log('Registered')
    })
     }
     render()
     {
         return(
             <div>
            {
                this.ques.map((quest ,index)=>{
                    return(
                        <div>
                        <h6>Question {index}:</h6>
                        <br></br>
                        <h3>{quest.value}</h3>
                        <textarea name="second" rows="5" cols="100" title="Answer" onChange={this.onChangeInput} ></textarea>
                        <br></br>
                        </div>
                    );
                })
            }
             <h6>Question 1:</h6>
             <br></br>
             <h3>Some Question</h3>
             <textarea name="second" rows="5" cols="100" title="Answer" onChange={this.onChangeInput} ></textarea>
             <br></br>
             <button class="primary" onClick={this.onSubmit}>Submit</button>
             </div>
         );
     }
 }
 export default Input;
