import React from 'react';
import axios from 'axios';

 class Input extends React.Component{
     onChangeInput=(e)=>{
        window.localStorage.setItem('answer',e.target.value )
     }
     onSubmit=(e)=>{
        axios.post('http://localhost:5000/ans', {ans: window.localStorage.getItem('answer')})
    .then(response => {
      console.log('Registered')
    })
     }
     render()
     {
         return(
             <div>
             <h6>Question 1:</h6>
             <br></br>
             <h3>Some Question</h3>
             <textarea name="second" rows="5" cols="100" title="Answer" onChange={this.onChangeInput} ></textarea>
             <br></br>
             <button class="primary" onClick={this.onSubmit}>Submit</button>
             </div>
         );
     }
 }
 export default Input;
