import React from 'react';
import axios from 'axios';
import {questions} from './data/constants.js'

 class Input extends React.Component{
     constructor(props){
         super(props);
         this.ques =questions;
         this.ans =[[]];
     }
     onChangeInput=(e,index)=>{
         this.ans[index] =e.target.value;
        window.localStorage.setItem('answer',JSON.stringify(this.ans) );
     }
     onSubmit=(e)=>{
         console.log('submitting...')
        axios.post('http://localhost:5000/ans', {ans: window.localStorage.getItem('answer')})
    .then(response => {
      console.log('Registered')
    })
     }
     render()
     {
         return(
             <div>
            {
                this.ques.map((quest ,index)=>{
                    return(
                        <div>

                        <h3>Q{index+1} : {quest.value}</h3>
                        {
                            this.ans[index][0]=quest.id,
                            this.ans[index][1]=" "
                        }
                        <textarea name="second" rows="5" cols="100" title="Answer" onChange={(e)=>this.onChangeInput(e,index)} ></textarea>
                        <br></br>
                        <br></br>
                        </div>
                    );
                })
            }

             <br></br>
             <button class="primary" onClick={this.onSubmit}>Submit</button>
             </div>
         );
     }
 }
 export default Input;
